OM edge) as s0, edge s1 WHERE s0.trg = s1.src AND s0.label = hasReview AND s1.label = hasReview  UNION SELECT s0.src, s2.trg FROM edge s0, (SELECT trg as src, src as trg, label FROM edge) as s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = reviewer AND s1.label = author  AND s2.label = hasReview )) , c4(src, trg) AS (SELECT src, trg FROM c3 UNION SELECT head.src, tail.trg FROM c3 as head, c4 as tail WHERE head.trg = tail.src) SELECT DISTINCT c0.src, c3.trg  FROM c0, c1, c2, c3, c4 WHERE c1.src = c0.trg AND c2.src = c1.trg AND c3.src = c2.trg;
