WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 52)) , c1(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 52)) , c2(src, trg) AS ((SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 53)) SELECT DISTINCT c0.src, c0.trg  FROM c0, c1, c2 WHERE c2.src = c1.trg AND c0.src = c1.src AND c0.trg = c2.trg;
