WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 14)) , c1(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 76)) , c2(src, trg) AS ((SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 76)) , c3(src, trg) AS ((SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 53)) SELECT "true" FROM edge WHERE EXISTS (SELECT * FROM c0, c1, c2, c3 WHERE c3.src = c2.trg AND c2.src = c1.trg AND c1.src = c0.trg);
