WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 55)) , c1(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 61)) , c2(src, trg) AS ((SELECT edge.src, edge.src FROM edge UNION SELECT edge.trg, edge.trg FROM edge UNION SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 60)) , c3(src, trg) AS (SELECT src, trg FROM c2 UNION SELECT head.src, tail.trg FROM c2 as head, c3 as tail WHERE head.trg = tail.src) SELECT DISTINCT c0.trg , c0.src, c2.src FROM c0, c1, c2, c3 WHERE c2.src = c1.trg AND c0.src = c1.src AND c0.trg = c2.trg;
