WITH RECURSIVE c0(src, trg) AS ((SELECT edge.src, edge.src FROM edge UNION SELECT edge.trg, edge.trg FROM edge UNION SELECT s0.src, s0.trg FROM edge s0 WHERE s0.label = 60)) , c1(src, trg) AS (SELECT src, trg FROM c0 UNION SELECT head.src, tail.trg FROM c0 as head, c1 as tail WHERE head.trg = tail.src) , c2(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 80)) , c3(src, trg) AS ((SELECT s0.src, s0.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0 WHERE s0.label = 81)) SELECT "true" FROM edge WHERE EXISTS (SELECT * FROM c0, c1, c2, c3 WHERE c3.src = c2.trg AND c0.src = c2.src AND c0.trg = c3.trg);
