g = s2.src AND s2.trg = s3.src AND s0.label = isLocatedIn AND s1.label = name  AND s2.label = imageFile  AND s3.label = isSubclassOf )) SELECT "true" FROM edge WHERE EXISTS (SELECT * FROM c0, c1, c2, c3 WHERE c3.src = c2.trg AND c1.trg = c3.trg AND c1.src = c0.trg AND c0.src = c2.src);
