WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s3.trg FROM edge s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = publishedIn  AND s3.label = extendedTo )) , c1(src, trg) AS ((SELECT s0.src, s2.trg FROM edge s0, edge s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = heldIn  UNION SELECT s0.src, s2.trg FROM edge s0, edge s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = heldIn )) , c2(src, trg) AS ((SELECT s0.src, s2.trg FROM edge s0, edge s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = heldIn  UNION SELECT s0.src, s2.trg FROM edge s0, edge s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = heldIn  UNION SELECT s0.src, s2.trg FROM edge s0, edge s1, edge s2 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s0.label = authors AND s1.label = publishedIn  AND s2.label = heldIn )) SELECT DISTINCT c2.trg , c2.src, c1.trg , c0.src FROM c0, c1, c2 WHERE c2.src = c0.trg AND c0.src = c1.src;
